import { View, Text, Animated, ImageBackground, SafeAreaView, StyleSheet, Dimensions } from 'react-native';
import React, { useEffect, useState } from 'react';
import { useNavigation, CommonActions } from '@react-navigation/core';


export default function Splash() {

    const navigation = useNavigation();

    const [imageSelected, setImageSelected] = useState("");
    const firtBackground = require('../../assets/e62565ab99a0c36d4db5583b0ef01339.png');
    const secondBackground = require('../../assets/ultimato-b-1-e1553606166939.png')

    const[backgroundHeigh] = useState(new Animated.Value(240));
    const[backgroundHeigh2, setBackgroundHeigh2] = useState('0%');

    useEffect(() => {
        setTimeout(() => {
            setImageSelected(firtBackground);
            firstAnimation.start();
            setTimeout(() => {
                setImageSelected(secondBackground);
                secondAnimation.start();
                setTimeout(() => {
                    thirdAnimation.start();

                    // navigation.dispatch(CommonActions.navigate({
                    //     name: 'Login'
                    // }))
                }, 2000)
            }, 2000)
        }, 2000)
    }, []);

    const [posx] = useState(new Animated.Value(-130));
    const [posy] = useState(new Animated.Value(-250));
    const [scale] = useState(new Animated.Value(1));

    const [backposx] = useState(new Animated.Value(0));
    const [backposy] = useState(new Animated.Value(0));
    const [scaleBack] = useState(new Animated.Value(0));
    const [opacity] = useState(new Animated.Value(1));

    const def = {
        transform: [
            { translateX: posx },
            { translateY: posy },
            { scaleX: scale },
            { scaleY: scale },
        ],
        backgroundColor: 'green', width: 100, height: 100, paddingTop: 30, margin: 10
    }

    //FIRST ANIMATION
    const firstAnimation = Animated.parallel([
        Animated.timing(posy, {
            toValue: Dimensions.get('screen').height / 3,
            duration: 700,
            useNativeDriver: true,
        }),
        Animated.timing(posx, {
            toValue: Dimensions.get('screen').width / 8,
            duration: 700,
            useNativeDriver: true,
        }),
    ]);

    const secondAnimation = Animated.parallel([
        Animated.timing(posy, {
            toValue: (Dimensions.get('screen').height / 3) / 3,
            duration: 700,
            useNativeDriver: true,
        }),

        Animated.timing(posx, {
            toValue: ((Dimensions.get('screen').width) / 2.7) * -1,
            duration: 700,
            useNativeDriver: true,
        }),
    ])

    const thirdAnimation = Animated.parallel([
        Animated.timing(posy, {
            toValue: (Dimensions.get('screen').height / 4.5) * -1,
            duration: 700,
            useNativeDriver: true,
        }),
        Animated.timing(scaleBack,{
            toValue: 300,
            duration: 700,
            useNativeDriver: true,
        })
    ]);



    const backendProps = {
        transform: [
            { scaleY: scaleBack },
        ],
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#000',
        width: '100%',
        height: 240,
    }

    const form = {
        transform: [
            { translateY: scaleBack },
        ],
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#000',
        width: '100%',
        height: scaleBack,
    }

    const background = {
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '100%', 
    }


    return (
        <SafeAreaView style={styles.container}>
            <Animated.View style={form}>
                
            </Animated.View>
             <View style={background}>
                <ImageBackground source={imageSelected} resizeMode='cover' style={styles.image}>
                    {/* <Animated.View style={def}></Animated.View>  */}
                </ImageBackground>
            </View>  
        </SafeAreaView>

    )
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: '#000fff',
        width: '100%',
        height: '100%',
    },
    image: {
        justifyContent: "center",
        alignItems: 'center',
        width: '100%', height: '100%',
    }
})
